import React, { Component } from 'react';
import './App.css';
import Intro from "../Intro";
import SeriesList from "../SeriesList";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      movieList: []
    }
  }


  componentDidMount(){
    fetch(`http://api.tvmaze.com/search/shows?q=girls`)
    .then(response => response.json())
    .then(result => this.setState({ movieList: result}))
  }


  render(){
    console.log(this.state.movieList)
    const { movieList } = this.state;
    return (
      <div className="App">
        <header className="App-header">
          <p>TV Series App</p>
        </header>

        <Intro messsage="Here you can find your favourite TV Series App" /> 

        <p>The length of movie {this.state.movieList.length}</p>

        <div className="App-seriesList">
          <ul>
            {movieList.map(item =>(
                <li>{item.show.name}</li>
              ))}
           </ul> 
        </div> 

        <SeriesList demo="text" />     

      </div>
    );
}
}



export default App;
